function output = d_getgoodcolour(input)% this returns colour vectors so that i can have decent colours for graphs% knows red, green, blue, orange, purple, pink, brown, blackwhile input > 8	input = input - 8;endswitch inputcase 1	%red	r = 1;	g = 0;	b = 0;case 2	%green	r = 0;	g = 1;	b = 0;case 3	%blue	r = 0;	g = 0;	b = 1;case 4	%orange	r = 1;	g = 0.5;	b = 0;case 5	%purple	r = 0.6;	g = 0.2;	b = 1;case 6	%pink	r = 1;	g = 0.4;	b = 0.8;case 7	%brown	r = 0.6;	g = 0.3;	b = 0.3;case 8	%black	r = 0;	g = 0;	b = 0;    %         % now aquamarine%     r = 0;%     g = 0.8;%     b = 0.7;endoutput(1) = r;output(2) = g;output(3) = b;return