function [l] = d_linetype(a)% creates a matrix of strings of different line style variables to use with plot function% ordered in columns by colour, symbol, stylel(1,1) = 'y';l(1,2) = 'm';l(1,3) = 'c';l(1,4) = 'r';l(1,5) = 'g';l(1,6) = 'b';l(1,7) = 'k';for a = 1:7	l(1,a+7) = l(1,a);end% white ommitted because using white background at the mol(2,1) = '.';l(2,2) = 'o';l(2,3) = 'x';l(2,4) = '+';l(2,5) = '*';l(2,6) = 's';l(2,7) = 'd';l(2,8) = 'v';l(2,9) = '<';l(2,10) = '>';l(2,11) = 'p';l(2,12) = 'h';l(3,1) = '-';l(3,2) = ':';return